class AddressBook{
    constructor(fName,lName,address,city,state,zip,phoneNumber){
        this.fName = fName
        this.lName = lName
        this.address = address
        this.city = city
        this.state = state
        this.zip = zip
        this.phoneNumber = phoneNumber
    }
}
let addBook = new AddressBook("anurag","bhardwaj","achrol","city","rajastha",4645,997877)
console.log(addBook.fName)
console.log(addBook.lName)
console.log(addBook.address)
